import React from 'react';
import styled from '@emotion/styled';
import {useState} from 'react';
import {PopupBackgroundContainer} from '../NavBar/NavBarItem';
import {PopupContent} from '../NavBar/NavBarItem';

export default function PhotoGrid({photos}:any){
    const [photoEnlarged,setPhotoEnlarged] = useState(false);
    const togglePhoto = () => setPhotoEnlarged(!photoEnlarged);

    return(
        <>
            {photoEnlarged && 
            <PopupBackgroundContainer onClick={togglePhoto}>
                <PopupContent>
                    <ImageContainer src = {photo.fields.photo.fields.file.url}/>
                </PopupContent>
            </PopupBackgroundContainer>
            }
            <PhotoGridContainer>
                {photos && photos.data.map((photo:any)=>
                    <PhotoContainer onClick={togglePhoto} key={photo.sys.id}>    
                        <ImageContainer src = {photo.fields.photo.fields.file.url}/>
                        {/* todo: on click, photo enlarges and caption is shown */}
                        {/* <p>{photo.fields.photo.fields.description}</p> */}
                    </PhotoContainer>
                    )
                }
            </PhotoGridContainer>
        </>

    )
}

const PhotoGridContainer=styled.div`
    display: flex;
    flex-wrap: wrap;
`

const PhotoEnlarged=styled.div`

`

export const PhotoContainer=styled.div`
    border: 5px solid white;
    max-width: 300px;
    max-height: 300px;
    background-color: white;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    margin-bottom: 25px;
    padding: 10px;
    margin-left: 5px;
    margin-right: 5px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
`
export const ImageContainer = styled.img`
    max-width: 100%;
    max-height: 100%;
`
