import React from 'react';
import Link from 'next/link';
import styled from '@emotion/styled';

const PostCard = ({post}:any) => {
    const {postTitle,postMainImage,postCreationDate,postUrl} = post.fields;



    return (
        <Link href={postUrl}>
            <PostContainer>
                <a>
                <PostImage src = {postMainImage.fields.file.url}/>
                <PostTitle>{postTitle}</PostTitle>
                <PostDate>{postCreationDate}</PostDate>
                </a>

            </PostContainer>

        </Link>
    )
  };


const PostContainer = styled.div`
    border: 5px solid white;
    width: 300px;
    height: 300px;
    background-color: white;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    margin-bottom: 25px;
    padding: 5px 10px;
`

const PostImage = styled.img`
    width: 100%;
    max-height: 75%;
`
const PostTitle = styled.h2`
    margin: 0;
    padding: 5px;
    text-align: center;
    font-family: 'Karla';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: local('Karla'), local('Karla-Regular'), url(https://fonts.gstatic.com/s/karla/v7/qkBbXvYC6trAT7RbLtyU5rZPoAU.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
`
const PostDate = styled.h3`
    margin: 0;
    text-align: center;
    font-family: 'Karla';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: local('Karla'), local('Karla-Regular'), url(https://fonts.gstatic.com/s/karla/v7/qkBbXvYC6trAT7RbLtyU5rZPoAU.woff2) format('woff2');
    unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;

`
  export default PostCard
