import React from 'react';
import Link from 'next/link';
import styled from '@emotion/styled';

const PostCard = ({post}:any) => {
    const {postTitle,postMainImage,postCreationDate,postUrl} = post.fields

    return(
        <Link href={postUrl}>
            <div style={postStyle}>
                <a>
                    <div>
                        <img style ={photoStyle}src = {postMainImage.fields.file.url}/>
                    </div>
                    <h2>{postTitle}</h2>
                    <h3>{postCreationDate}</h3>
                </a>
            </div>
        </Link>
    )
  };

const postStyle = {
    margin: '40px',
    border: '5px solid pink',
    height: '300px',
    width: '300px',
    position: "relative"
};

const photoStyle = {
    height: '250px',
    width: '100%'
}
const PostCardStyle = styled(PostCard)`
  color: red;
`
  export default PostCard
