import React from 'react';
import Link from 'next/link';
import styled from '@emotion/styled';

const PostCard = ({post}:any) => {
    const {postTitle,postMainImage,postCreationDate,postUrl} = post.fields

    return(
        <Link href={postUrl}>
            <div style={postStyle}>
                    {/* <div style={photoStyle}>
                        <img src = {postMainImage.fields.file.url}/>
                    </div> */}
                    <img style={photoStyle} src = {postMainImage.fields.file.url}/>
                    <h2>{postTitle}</h2>
                    <h3>{postCreationDate}</h3>
            </div>
        </Link>
    )
  };

const postStyle = {
    margin: '40px',
    border: '5px solid pink',
    height: '300px',
    width: '300px',
    position: "relative",
    color: 'black',
    display: "flex",
    justifycontent: "center",
    alignitems: "center",
    overflow: "hidden"
};

const photoStyle = {
    // flexshrink: "0",
    // minwidth: "100%",
    // minheight: "100%"
}
const PostCardStyle = styled(PostCard)`
  color: red;
`
  export default PostCard
