import styled from '@emotion/styled';

export const PostContainer = styled.div`
    font-family: "Roboto", Arial, sans-serif;
    margin: 10px;
    cursor: pointer;
    flex: 0 0 50%;
    max-width: 300px;
    max-height: 300px;
    transition: .3s all ease;
    margin-bottom: 30px;
    position: relative;
`

export const PostImageContainer = styled.div`
    overflow: hidden;
    max-height: 250px;
    max-width: 250px;
`

export const PostImage = styled.img`
    width: 100%;
    height: 100%;
    object-fit: contain;
    // overflow: hidden;
    // max-width: 100%;
    // vertical-align: middle;
    // border-style: none;
    // max-height: 400px;
    // max-width: 400px;
    // display: block;
    // z-index: -1;
    // // width: 100%;
    // overflow: hidden;
    // position: relative;
    // background-size: cover;
    // background-position: center center;
    // background-repeat: no-repeat;
    // box-sizing: border-box;
    // transition: transform .5s ease;
    // &:hover{
    //     transform: scale(1.5);
    // }
    // width: 100%;
    // max-height: 75%;
`
export const PostDescription=styled.div`
    padding: 20px;
    border: 1px solid #efefef;
    border-top: none;
    `
export const PostTitle = styled.h2`
    font-weight: 700;
    font-size: 28px;
    margin: 0 0 20px 0;
    // margin: 0;
    // padding: 5px;
    text-align: left;
`
export const PostDate = styled.h3`
    text-align: center;
`
export const PostGridContainer=styled.div`
    padding-top: 5%;
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
`
