import NavBar from '../components/NavBar/NavBar';
import React from 'react';
import styled from '@emotion/styled';
import {PhotoContainer,ImageContainer} from '../components/PhotoGrid/components';
import { url } from 'inspector';

const Post = (data: any) => {
    const {postTitle,postMainImage,postCreationDate,postBody} = data
    return(
        data && 
        <div>
            <NavBar/>
            <PostHeader style={{backgroundImage: url(postMainImage.fields.file.url)}} >
                <PostTitleContainer>
                    {postTitle}
                </PostTitleContainer>
                    {/* <PostHeaderImage src = {postMainImage.fields.file.url}/> */}
            </PostHeader>

                <PostDateContainer>
                    {postCreationDate}
                </PostDateContainer>
                <PostBodyContainer>
                    {postBody}
                </PostBodyContainer>  
        </div>
    )
}

interface BackgroundImage {
    photo: URL;
}



export const PostTitleContainer=styled.h2`
    background: white;
    text-align: center;
    position: absolute;
    padding: 10px px 10px 10px;
    bottom: -18px;
    right: 45px;
    border-bottom: 1px solid #e6e6e6;
`
const PostDateContainer=styled.h3`
    text-align: center;
    font-family: 'Inconsolata';
`
// const PostHeader = styled.div<BackgroundImage>(({ photo = '' }) =>{
//     var photostr = String(photo);
//     console.log(photostr.substring(2))

//     return(
//         `
//         background: url(${photostr});
//         `
//     )
// })
// `
//     background: url(${photo});
//     position: relative;
//     text-align: center;
// `)

const PostHeader=styled.div`
    position: relative;
    text-align: center;
`


const PostHeaderImage=styled.img`
    width: 100%;
    height: 100%;
`

export const PostBodyContainer=styled.p`
    font-family: 'Inconsolata';
    text-align: left;
    margin-left: 20%;
    margin-right: 20%;
`

Post.getInitialProps = (context:any) => context.query.post.fields;

export default Post
