import NavBar from '../components/NavBar/NavBar';
import React from 'react';
import styled from '@emotion/styled';
import {PhotoContainer,ImageContainer} from '../components/PhotoGrid/components';
import { url } from 'inspector';

const Post = (data: any) => {
    const {postTitle,postMainImage,postCreationDate,postBody} = data
    return(
        data && 
        <div>
            <NavBar/>
            <PostHeader background = {postMainImage.fields.file.url}>
            <PostTitleContainer>
                    {postTitle}
                </PostTitleContainer>
                {/* <PhotoContainer style={{
                    margin: "auto",
                    width: "50%",
                    cursor: "auto"
                }}> */}
                    {/* <PostHeaderImage src = {postMainImage.fields.file.url}/> */}
                {/* </PhotoContainer> */}
            </PostHeader>

                <PostDateContainer>
                    {postCreationDate}
                </PostDateContainer>
                <PostBodyContainer>
                    {postBody}
                </PostBodyContainer>  
        </div>
    )
}

interface PostHeaderProps{
    background?: any;
}

export const PostTitleContainer=styled.h2`
    text-align: center;
    color: black;
    position: absolute;
    bottom: 8px;
    left: 16px;
`
const PostDateContainer=styled.h3`
    text-align: center;
    font-family: 'Inconsolata';
`
// const PostHeader=styled.div<PostHeaderProps>(({background = "true"}))=>`
//     position: relative;
//     text-align: center;
// `
const PostHeader = styled.div<PostHeaderProps>(({ background = url() }) => `
    position: relative;
    text-align: center;
    background: url(${background});
`);


const PostHeaderImage=styled.img`
    width: 100%;
    height: 100%;
`

export const PostBodyContainer=styled.p`
    font-family: 'Inconsolata';
    text-align: left;
    margin-left: 20%;
    margin-right: 20%;
`

Post.getInitialProps = (context:any) => context.query.post.fields;

export default Post
