import React from 'react';
import PostCard from './PostCard';


const handleClick = (post) => {
    const correctDog = currentDoggies[correctDoggieIndex].breed;
    const isCorrect = correctDog === breed;
    addSelectedDog(breed);
    if(isCorrect){
      setTimeout(()=>{
        loadAllDogs();
      },450);
    }
  }


export default function PostGrid({posts}){

    return(
        <div>
            <h1>BLOG TITLE</h1>    
            {posts && posts.map(post=><PostCard key={post.id} handleClick = {(post)=>{handleClick()}}>{post}</PostCard>)}

        </div>
    )
}
