import React from 'react';
import Link from 'next/link';
import {useState} from 'react';
import styled from '@emotion/styled';


const NavBarItem = (props:any) => {

  const [contactIsShown,setContactIsShown] = useState(false);
  const hide = () => setContactIsShown(false);
  const show = () => setContactIsShown(true);

  const linkName = props.children.toLowerCase()
  if(linkName === 'home'){
    return (
      <div>
        <Link href ={'./'} >
        <NavBarItemContainer>
          <a>
            {props.children}
          </a>
        </NavBarItemContainer>
      </Link>
      </div>
    )
  }

  if(linkName === 'contact'){

    return (
      <div>
        <button onClick = {show}>{props.children}</button>
        {contactIsShown && 
        <div>
          Email: ktconnell99@gmail.com
          <button onClick ={hide}>X</button>
        </div>}
      </div>
    );
  }
  return (
    <div>
      <Link href ={'/' + linkName}>
      <NavBarItemContainer>
        <a>
          {props.children}
        </a>
      </NavBarItemContainer>
      </Link>
    </div>
  );
};

const NavBarItemContainer = styled.div`
  text-decoration: none;
  cursor: pointer;
  padding: 10px;
  transition: all .3s;
  &:hover{
    color: white;
    background: #bccbde;
  }
  display: inline-block;
  float: left;
`

export default NavBarItem
