import { FilterBar } from "./FilterBar";
import { filter } from "compression";

export const FilterButton = ({tags}:any) =>{
    return(
        <div>
            <button onClick={()=>showFilterBar()}>Filter</button>
            <FilterBar id="filterBar"tags={tags}></FilterBar>
        </div>
    )
}

const showFilterBar=()=>{
    document.getElementById("filterBar").setAttribute("style","display: none");
}
