import styled from '@emotion/styled';

export const PhotoContainer=styled.div`
    max-height:300px;
    max-width: 300px;
    margin: 10px;
    border: 1px solid white;
    position: relative;
    cursor: pointer;
`
export const ImageContainer = styled.img`
    object-fit: cover;
    width: 100%;
    height: 100%;
    overflow: hidden;
    display: block; 
`

export const PhotoGridContainer=styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
`

export const CaptionOverlay=styled.div`
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    height: 100%;
    width: 100%;
    opacity: 0;
    transition: .5s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: white;
    &:hover{
        opacity: 1;
        border: 1px solid #efefef;
    }
`

export const Modal = styled.div`
    position: fixed; /* Stay in place */
    z-index: 1; /* Sit on top */
    padding-top: 100px;
    left: 0;
    top: 0;
    width: 100%; /* Full width */
    height: 100%; /* Full height */
    overflow: auto;
    background-color: rgba(0,0,0,0.9);
`
export const ModalClose = styled.div`
    position: absolute;
    top: 15px;
    right: 35px;
    color: #f1f1f1;
    font-size: 40px;
    font-weight: bold;
    transition: 0.3s;
    &:hover,&:focus{
        color: #bbb;
        text-decoration: none;
        cursor: pointer; 
    }
`
export const ModalContent = styled.img`
    margin: auto;
    display: block;
    width: 80%;
    max-width: 700px;

`
export const ModalCaption = styled.div`
    animation-name: zoom;
    animation-duration: 0.6s;
    margin: auto;
    display: block;
    width: 80%;
    max-width: 700px;
    text-align: center;
    color: #ccc;
    padding: 10px 0;
    height: 150px;
`
