import React from 'react';
import Link from 'next/link';
import {useState} from 'react';
import styled from '@emotion/styled';


const NavBarItem = (props:any) => {

  const [contactIsShown,setContactIsShown] = useState(false);
  const togglePopup = () => setContactIsShown(!contactIsShown);

  const handleChildClick = (e:any) =>{
    e.stopPropagation();
  }

  const linkName = props.children.toLowerCase()
  if(linkName === 'home'){
    return (
      <div>
        <Link href ={'./'} >
        <NavBarItemContainer>
          <a>
            {props.children}
          </a>
        </NavBarItemContainer>
      </Link>
      </div>
    )
  }

  if(linkName === 'contact'){
    return (
      <div>
          <NavBarButtonContainer onClick = {togglePopup}>{props.children}</NavBarButtonContainer>
        {contactIsShown && 
        <PopupBackgroundContainer onClick ={togglePopup}>
          <PopupContent onClick ={handleChildClick}>
            Email: ktconnell99@gmail.com
          </PopupContent>
        </PopupBackgroundContainer>}
      </div>
    );
  }
  return (
    <div>
      <Link href ={'/' + linkName}>
      <NavBarItemContainer>
        <a>
          {props.children}
        </a>
      </NavBarItemContainer>
      </Link>
    </div>
  );
};

const NavBarItemContainer = styled.div`
  color: white;
  text-decoration: none;
  cursor: pointer;
  padding: 10px;
  width: 100%;
  display: inline-block;
  transition: all .4s;
  text-align: center;
  &:hover{
    background: #bccbde;
  }
`
const NavBarButtonContainer = styled.button`
  border: none;
  background: transparent;
  color: white;
  cursor: pointer;
  padding: 10px;
  width: 100%;
  transition: all .4s;
  text-align: center;
  &:hover{
    background: #bccbde;
  }
`

export const PopupBackgroundContainer = styled.div` 
  position: fixed;
  width: 100%;  
  height: 100%;  
  top: 0;  
  left: 0;  
  right: 0;  
  bottom: 0;  
  margin: auto;  
  background-color: rgba(0,0,0, 0.5);   
`
export const PopupContent=styled.div`
  text-align: center; 
  line-height: 12;
  background-color: white;
  position: fixed;
  width: 500px;
  height: 200px;
  top: 50%;
  left: 50%;
  margin-top: -100px;
  margin-left: -250px;
`


export default NavBarItem
