import styled from '@emotion/styled';

export const PostContainer = styled.div`
    // border: 5px solid white;
    // width: 300px;
    // height: 300px;
    // background-color: white;
    // box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    // margin-bottom: 25px;
    // padding: 5px 10px;
    // margin-left: 5px;
    // margin-right: 5px;
    // cursor: pointer;
`

export const PostImage = styled.img`
    max-height: 300px;
    display: block;
    z-index: -1;
    width: 100%;
    overflow: hidden;
    position: relative;
    background-size: cover;
    background-position: center center;
    background-repeat: no-repeat;
    box-sizing: border-box;
}






    // width: 100%;
    // max-height: 75%;
`
export const PostTitle = styled.h2`
    font-weight: 700;
    font-size: 28px;
    margin: 0 0 20px 0;
    // margin: 0;
    // padding: 5px;
    // text-align: center;
`
export const PostDate = styled.h3`
    padding-right: 10px;        
    // margin: 0;
    // text-align: center;
`
export const PostGridContainer=styled.div`
    z-index: 0;
    display: block;
    position: relative;
    font-weight: 300;
    font-size: 16px;
    line-height: 2;
    font-family: "Roboto", Arial, sans-serif;
    color: #666666;

    // padding-top: 5%;
    // display: flex;
    // flex-direction: row;
    // flex-wrap: wrap;
`
export const PostDescription=styled.div`
    width: 90%;
    background: #fff;
    margin-top: -3em;
    z-index: 1;
    padding: 1em;

    `
