import React from 'react';
import styled from '@emotion/styled';
import {PhotoContainer,ImageContainer} from '../components/PhotoGrid/components';

const Post = (data: any) => {
    const {postTitle,postMainImage,postCreationDate,postBody} = data
    return(
        data && 
        <div>
            <NavBar/>
            <PostHeader photo = {postMainImage.fields.file.url} >
                <PostTitleContainer>
                    {postTitle}
                </PostTitleContainer>
                    {/* <PostHeaderImage src = {postMainImage.fields.file.url}/> */}
            </PostHeader>

                <PostDateContainer>
                    {postCreationDate}
                </PostDateContainer>
                <PostBodyContainer>
                    {postBody}
                </PostBodyContainer>  
        </div>
    )
}

interface BackgroundImage {
    photo: URL;
}



export const PostTitleContainer=styled.h2`
    background: white;
    text-align: center;
    position: absolute;
    padding: 10px px 10px 10px;
    bottom: -18px;
    right: 45px;
    border-bottom: 1px solid #e6e6e6;
`
const PostDateContainer=styled.h3`
    text-align: center;
    font-family: 'Inconsolata';
`
