import React from 'react';
import Link from 'next/link';
import {useState} from 'react';
import {PopupBackgroundContainer, PopupContent} from '../Popups/Popups';
import {NavBarItemContainer} from './components';


const NavBarItem = (props:any) => {

  const [contactIsShown,setContactIsShown] = useState(false);
  const togglePopup = () => setContactIsShown(!contactIsShown);

  const handleChildClick = (e:any) =>{
    e.stopPropagation();
  }

  const linkName = props.children.toLowerCase()
  if(linkName === 'home'){
    return (
      <div>
        <Link href ={'./'} >
        <NavBarItemContainer>
          <a>
            {props.children}
          </a>
        </NavBarItemContainer>
      </Link>
      </div>
    )
  }

  if(linkName === 'contact'){
    return (
      <div>
          <NavBarItemContainer onClick = {togglePopup}>{props.children}</NavBarItemContainer>
        {contactIsShown && 
        <PopupBackgroundContainer onClick ={togglePopup}>
          <PopupContent onClick ={handleChildClick}>
            Email: ktconnell99@gmail.com
          </PopupContent>
        </PopupBackgroundContainer>}
      </div>
    );
  }
  return (
    <div>
      <Link href ={'/' + linkName}>
      <NavBarItemContainer>
        <a>
          {props.children}
        </a>
      </NavBarItemContainer>
      </Link>
    </div>
  );
};



export default NavBarItem
