import NavBar from '../components/NavBar/NavBar';
import React from 'react';
import styled from '@emotion/styled';

const Post = (data: any) => {
    const {postTitle,postMainImage,postCreationDate,postBody} = data
    return(
        data && 
        <div>
            <NavBar/>
                <PostTitleContainer>
                    {postTitle}
                </PostTitleContainer>
                <div>
                    <PostMainImage src = {postMainImage.fields.file.url}/>
                </div>
                <h3>
                    {postCreationDate}
                </h3>
                <p>
                    {postBody}
                </p>  
        </div>
    )
}

const PostTitleContainer=styled.h2`
    text-align: center;
`
const PostMainImage=styled.img`
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 50%;
`


Post.getInitialProps = (context:any) => context.query.post.fields;

export default Post
