import React from 'react';
import Link from 'next/link';
import { any } from 'prop-types';

const handleClick = () =>{
  return(
    <div style={tooltipStyle}>
      Email: ktconnell99@gmail.com
    </div>
  )

}

const tooltipStyle = {
  display: this.state.hover ? 'block' : 'none'
}

const NavBarItem = (props:any) => {

    const linkName = props.children.toLowerCase()
    if(linkName === 'home'){
      return (
        <div>
          <Link href ={'./'} >
          <a>
            {props.children}
          </a>
       </Link>
        </div>
      )
    }

    if(linkName === 'contact'){

      return (
        <div>
          <div onClick={handleClick}>on click here we will show the tooltip</div>
          <div>
            <div style={tooltipStyle}>this is the tooltip!!</div>
          </div>
        </div>
      );
    }
    return (
      <div>
       <Link href ={'/' + linkName}>
        <a>
            {props.children}
        </a>
       </Link>
      </div>
    );
  };

  export default NavBarItem
